namespace Cytracon\NinjaMenus\Model\Source;

class MobileMenuType implements \Magento\Framework\Data\OptionSourceInterface
{
    /**
     * Get options
     *
     * @return array
     */
    public function toOptionArray()
    {
        $options[] = [
            'label' => __('Accordion'),
            'value' => 'accordion',
        ];
        $options[] = [
            'label' => __('Drilldown'),
            'value' => 'drilldown'
        ];
        return $options;
    }
}
